    if (cell === 1) {
      className += ' wall';
    } else if (cell === 2) {
      content = '¬∑';
    } else if (cell === 3) {
      content = '‚óè';
      className += ' power-pellet';
    }

    // Renderizar Shiro Neko con imagen personalizada y efectos
    if (shiroPosition.x === x && shiroPosition.y === y) {
