      className += ' shiro';
      if (powerMode) className += ' power-mode';
      if (speedBoost) className += ' speed-boost';
      if (fireMode) className += ' fire-mode';
      if (ghostMode) className += ' ghost-mode';
      if (shieldActive) className += ' shield-mode';
      if (rainbowMode) className += ' rainbow-mode';
    }

    // Renderizar power-ups
    const powerUpAtPosition = powerUps.find(powerUp => powerUp.x === x && powerUp.y === y);
    if (powerUpAtPosition && !(shiroPosition.x === x && shiroPosition.y === y)) {
      content = powerUpAtPosition.emoji;
      className += ' power-up';
    }

    // Renderizar fantasmas
    const ghostAtPosition = ghosts.find(ghost => ghost.x === x && ghost.y === y);
    if (ghostAtPosition && !(shiroPosition.x === x && shiroPosition.y === y)) {
      content = ghostAtPosition.type.emoji;
      className += ` ghost ${ghostAtPosition.mode}`;
      if (ghostAtPosition.mode === 'frightened') {
        className += ' frightened';
      }
    }

    return (
      <div key={`${x}-${y}`} className={className}>
        {content}
      </div>
    );
  };

  const getGameStateMessage = () => {
