            <div className="stat-item">Fantasmas comidos: {ghostsEaten}</div>
            <div className="stat-item">Mejor combo: {combo}</div>
            <div className="stat-item">Tiempo jugado: {formatTime(gameTime)}</div>
            <div className="stat-item">Puntos por minuto: {gameTime > 0 ? Math.round(score / (gameTime / 60)) : 0}</div>
          </div>
        )}
      </div>

      <div className="game-footer">
        <div className="ghost-info">
          <div className="ghost-legend">
            <div className="ghost-card">
              <span>üëª {GHOST_TYPES.BLINKY.name}</span>
              <small>{GHOST_TYPES.BLINKY.personality}</small>
            </div>
            <div className="ghost-card">
              <span>üü£ {GHOST_TYPES.PINKY.name}</span>
              <small>{GHOST_TYPES.PINKY.personality}</small>
            </div>
            <div className="ghost-card">
              <span>üîµ {GHOST_TYPES.INKY.name}</span>
              <small>{GHOST_TYPES.INKY.personality}</small>
            </div>
            <div className="ghost-card">
              <span>üü† {GHOST_TYPES.CLYDE.name}</span>
              <small>{GHOST_TYPES.CLYDE.personality}</small>
            </div>
          </div>
        </div>

        <div className="power-ups-info">
          <h4>Power-ups Disponibles:</h4>
          <div className="power-up-legend">
            <span>‚ö° Speed Boost</span>
            <span>üíñ Vida Extra</span>
            <span>üíé Puntos Dobles</span>
            <span>‚ùÑÔ∏è Congelar</span>
            <span>üî• Modo Fuego</span>
            <span>üëª Modo Fantasma</span>
            <span>‚è∞ Tiempo Lento</span>
            <span>üß≤ Im√°n</span>
            <span>üõ°Ô∏è Escudo</span>
            <span>üå™Ô∏è Tornado</span>
            <span>‚ö° Rayo</span>
            <span>üåà Arco√≠ris</span>
          </div>
        </div>
      </div>

      {/* Controles t√°ctiles para m√≥vil */}
      {isMobile && renderMobileControls()}

      {/* Pantalla de muerte */}
      {renderDeathScreen()}
    </div>
  );

  // Renderizar controles m√≥viles
  function renderMobileControls() {
    if (gameState !== GAME_STATES.PLAYING && gameState !== GAME_STATES.PAUSED) return null;

    return (
      <div className="mobile-controls">
        <div className="mobile-control-instructions">
          <div className="swipe-hint">
            <div className="swipe-arrows">
              <div className="swipe-arrow up">‚¨ÜÔ∏è</div>
              <div className="swipe-arrow-row">
                <div className="swipe-arrow left">‚¨ÖÔ∏è</div>
                <div className="swipe-center">üì±</div>
                <div className="swipe-arrow right">‚û°Ô∏è</div>
              </div>
              <div className="swipe-arrow down">‚¨áÔ∏è</div>
            </div>
            <div className="swipe-text">Desliza para mover</div>
          </div>

          <div className="mobile-buttons">
            <button
              className="mobile-btn pause-btn"
              onClick={() => {
                if (gameState === GAME_STATES.PLAYING) {
                  setGameState(GAME_STATES.PAUSED);
                } else if (gameState === GAME_STATES.PAUSED) {
                  setGameState(GAME_STATES.PLAYING);
                }
              }}
            >
              {gameState === GAME_STATES.PLAYING ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
            </button>

            {showStats && (
              <button
                className="mobile-btn stats-btn"
                onClick={() => setShowStats(false)}
              >
                ‚ùå
              </button>
            )}

            {!showStats && (
              <button
                className="mobile-btn stats-btn"
                onClick={() => setShowStats(true)}
              >
                üìä
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
