  BLINKY: { emoji: 'üëª', color: '#ff0000', speed: 180 }, // Rojo - Agresivo
  PINKY: { emoji: 'üü£', color: '#ffb8ff', speed: 200 },  // Rosa - Emboscada
  INKY: { emoji: 'üîµ', color: '#00ffff', speed: 220 },   // Azul - Impredecible
  CLYDE: { emoji: 'üü†', color: '#ffb852', speed: 240 }   // Naranja - T√≠mido
};

// Estados del juego
const GAME_STATES = {
  READY: 'ready',
  PLAYING: 'playing',
  PAUSED: 'paused',
  GAME_OVER: 'game_over',
  LEVEL_COMPLETE: 'level_complete',
  POWER_MODE: 'power_mode'
};

function App() {
  const [shiroPosition, setShiroPosition] = useState({ x: 9, y: 15 });
  const [maze, setMaze] = useState(MAZE);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [level, setLevel] = useState(1);
  const [gameState, setGameState] = useState(GAME_STATES.READY);
  const [direction, setDirection] = useState({ x: 0, y: 0 });
  const [powerMode, setPowerMode] = useState(false);
  const [powerModeTimer, setPowerModeTimer] = useState(0);
  const [ghosts, setGhosts] = useState([
    { id: 'blinky', x: 9, y: 9, direction: { x: 1, y: 0 }, type: GHOST_TYPES.BLINKY, mode: 'chase' },
    { id: 'pinky', x: 10, y: 9, direction: { x: -1, y: 0 }, type: GHOST_TYPES.PINKY, mode: 'chase' },
    { id: 'inky', x: 9, y: 10, direction: { x: 0, y: 1 }, type: GHOST_TYPES.INKY, mode: 'chase' },
    { id: 'clyde', x: 10, y: 10, direction: { x: 0, y: -1 }, type: GHOST_TYPES.CLYDE, mode: 'chase' }
  ]);
  const [totalDots, setTotalDots] = useState(0);
  const [dotsEaten, setDotsEaten] = useState(0);
  const gameLoopRef = useRef();
  const powerModeRef = useRef();

