          const newY = ghost.y + move.y;
          return newX >= 0 && newX < GRID_SIZE && 
                 newY >= 0 && newY < GRID_SIZE && 
                 maze[newY][newX] !== 1;
        });
        
        if (possibleMoves.length === 0) return ghost;
        
        let nextMove;
        
        if (ghost.mode === 'frightened') {
          // Movimiento aleatorio cuando est√°n asustados
          nextMove = possibleMoves[Math.floor(Math.random() * possibleMoves.length)];
        } else {
          // IA b√°sica: perseguir a Shiro Neko
          const distanceToShiro = possibleMoves.map(move => {
            const newX = ghost.x + move.x;
            const newY = ghost.y + move.y;
            return {
              move,
              distance: Math.abs(newX - shiroPosition.x) + Math.abs(newY - shiroPosition.y)
            };
          });
          
          nextMove = distanceToShiro.reduce((closest, current) => 
            current.distance < closest.distance ? current : closest
          ).move;
        }
        
        return {
          ...ghost,
          x: ghost.x + nextMove.x,
          y: ghost.y + nextMove.y,
          direction: nextMove
        };
      })
    );
  }, [gameState, maze, shiroPosition]);

  useEffect(() => {
    const handleKeyPress = (e) => {
      // Prevenir conflictos con extensiones
      if (!e || !e.key) return;
      
      e.preventDefault();
      
      switch(e.key) {
        case 'ArrowUp':
          setDirection({ x: 0, y: -1 });
          break;
        case 'ArrowDown':
          setDirection({ x: 0, y: 1 });
          break;
        case 'ArrowLeft':
          setDirection({ x: -1, y: 0 });
          break;
        case 'ArrowRight':
          setDirection({ x: 1, y: 0 });
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      if (direction.x !== 0 || direction.y !== 0) {
        moveShiro(direction);
      }
    }, 200);

    return () => clearInterval(interval);
  }, [direction, moveShiro]);

  const renderCell = (cell, x, y) => {
    let className = 'cell';
    let content = '';
    
    if (cell === 1) className += ' wall';
    else if (cell === 2) content = '¬∑';
    else if (cell === 3) content = '‚óè';
    
    if (shiroPosition.x === x && shiroPosition.y === y) {
      content = 'üê±';
      className += ' shiro';
    }
    
    return (
      <div key={`${x}-${y}`} className={className}>
        {content}
      </div>
    );
  };

  return (
    <div className="App">
      <div className="game-info">
        <h1>Shiro Neko Pacman</h1>
        <div className="score">Puntuaci√≥n: {score}</div>
        <div className="controls">Usa las flechas para mover a Shiro Neko</div>
      </div>
      <div className="game-board">
        {maze.map((row, y) =>
          row.map((cell, x) => renderCell(cell, x, y))
        )}
      </div>
    </div>
  );
}

export default App;
