import React from 'react';
import './App.css';
import GameBoard from './components/GameBoard';
import GameStats from './components/GameStats';
import GameInfo from './components/GameInfo';
import MobileControls from './components/MobileControls';
import { useGameLogic } from './hooks/useGameLogic';
import { useGameControls } from './hooks/useGameControls';
import { useGameEffects } from './hooks/useGameEffects';
import { GAME_STATES } from './constants/gameConstants';

function App() {
  // Usar hooks personalizados
  const gameLogic = useGameLogic();
  const gameControls = useGameControls(
    gameLogic.gameState,
    gameLogic.setGameState,
    gameLogic.setDirection,
    gameLogic.resetGame
  );

  // Efectos del juego
  useGameEffects({
    gameState: gameLogic.gameState,
    direction: gameLogic.direction,
    moveShiro: gameLogic.moveShiro,
    powerMode: gameLogic.powerMode,
    powerModeTimer: gameLogic.powerModeTimer,
    setPowerMode: (value) => gameLogic.powerMode = value,
    setPowerModeTimer: (value) => gameLogic.powerModeTimer = value,
    setGhosts: (value) => gameLogic.ghosts = value,
    setGameTime: (value) => gameLogic.gameTime = value,
    gameLoopRef: gameLogic.gameLoopRef,
    powerModeRef: gameLogic.powerModeRef
  });

  // Detectar móvil
  useEffect(() => {
    const dots = MAZE.flat().filter(cell => cell === 2 || cell === 3).length;
    setTotalDots(dots);
    
    const checkMobile = () => {
      const isMobileDevice = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ||
                           window.innerWidth <= 768 ||
                           ('ontouchstart' in window);
      setIsMobile(isMobileDevice);
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Verificar colisiones
  const checkGhostCollision = useCallback((position) => {
    return ghosts.some(ghost => ghost.x === position.x && ghost.y === position.y);
  }, [ghosts]);  
// Mover Shiro Neko
  const moveShiro = useCallback((newDirection) => {
    if (gameState !== GAME_STATES.PLAYING) return;
    
    setShiroPosition(prev => {
      const newX = prev.x + newDirection.x;
      const newY = prev.y + newDirection.y;
      
      if (newX < 0 || newX >= GRID_SIZE || newY < 0 || newY >= GRID_SIZE) return prev;
      if (maze[newY][newX] === 1) return prev;
      
      const newPosition = { x: newX, y: newY };
      
      // Verificar colisión con fantasmas
      if (checkGhostCollision(newPosition)) {
        if (powerMode) {
          setScore(s => s + 200);
          setGhosts(prevGhosts => 
            prevGhosts.map(ghost => 
              ghost.x === newX && ghost.y === newY 
                ? { ...ghost, x: 9, y: 9, mode: 'frightened' }
                : ghost
            )
          );
        } else {
          setLives(l => l - 1);
          if (lives <= 1) {
            if (score > highScore) {
              setHighScore(score);
              localStorage.setItem('shiroNekoHighScore', score.toString());
            }
            setGameState(GAME_STATES.GAME_OVER);
          } else {
            setShiroPosition({ x: 9, y: 15 });
            setGhosts([
              { id: 'blinky', x: 9, y: 9, direction: { x: 1, y: 0 }, type: GHOST_TYPES.BLINKY, mode: 'chase' },
              { id: 'pinky', x: 10, y: 9, direction: { x: -1, y: 0 }, type: GHOST_TYPES.PINKY, mode: 'chase' },
              { id: 'inky', x: 9, y: 10, direction: { x: 0, y: 1 }, type: GHOST_TYPES.INKY, mode: 'chase' },
              { id: 'clyde', x: 10, y: 10, direction: { x: 0, y: -1 }, type: GHOST_TYPES.CLYDE, mode: 'chase' }
            ]);
          }
          return prev;
        }
      }
      
      // Comer comida
      if (maze[newY][newX] === 2 || maze[newY][newX] === 3) {
        const isPowerPellet = maze[newY][newX] === 3;
        const points = isPowerPellet ? 50 : 10;
        setScore(s => s + points);
        setDotsEaten(d => d + 1);
        
        if (isPowerPellet) {
          setPowerMode(true);
          setPowerModeTimer(8000);
          setGhosts(prevGhosts => 
            prevGhosts.map(ghost => ({ ...ghost, mode: 'frightened' }))
          );
        }
        
        setMaze(prevMaze => {
          const newMaze = [...prevMaze];
          newMaze[newY] = [...newMaze[newY]];
          newMaze[newY][newX] = 0;
          return newMaze;
        });
      }
      
      return newPosition;
    });
  }, [gameState, maze, ghosts, powerMode, lives, checkGhostCollision, score, highScore]);  // Controles táctiles
  const handleTouchStart = useCallback((e) => {
    e.preventDefault();
    const touch = e.touches[0];
    setTouchStart({ x: touch.clientX, y: touch.clientY });
  }, []);

  const handleTouchEnd = useCallback((e) => {
    e.preventDefault();
    const touch = e.changedTouches[0];
    const deltaX = touch.clientX - touchStart.x;
    const deltaY = touch.clientY - touchStart.y;
    const minSwipeDistance = 30;
    
    if (Math.abs(deltaX) > Math.abs(deltaY)) {
      if (Math.abs(deltaX) > minSwipeDistance) {
        setDirection(deltaX > 0 ? { x: 1, y: 0 } : { x: -1, y: 0 });
      }
    } else {
      if (Math.abs(deltaY) > minSwipeDistance) {
        setDirection(deltaY > 0 ? { x: 0, y: 1 } : { x: 0, y: -1 });
      }
    }
  }, [touchStart]);

  // Controles del teclado
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (!e || !e.key) return;
      e.preventDefault();
      
      switch(e.key) {
        case 'ArrowUp': setDirection({ x: 0, y: -1 }); break;
        case 'ArrowDown': setDirection({ x: 0, y: 1 }); break;
        case 'ArrowLeft': setDirection({ x: -1, y: 0 }); break;
        case 'ArrowRight': setDirection({ x: 1, y: 0 }); break;
        case ' ':
          if (gameState === GAME_STATES.READY) setGameState(GAME_STATES.PLAYING);
          else if (gameState === GAME_STATES.PLAYING) setGameState(GAME_STATES.PAUSED);
          else if (gameState === GAME_STATES.PAUSED) setGameState(GAME_STATES.PLAYING);
          break;
        case 'r':
          if (gameState === GAME_STATES.GAME_OVER) {
            setShiroPosition({ x: 9, y: 15 });
            setMaze(MAZE);
            setScore(0);
            setLives(3);
            setLevel(1);
            setGameState(GAME_STATES.READY);
            setDirection({ x: 0, y: 0 });
            setPowerMode(false);
            setPowerModeTimer(0);
            setDotsEaten(0);
            setGameTime(0);
          }
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [gameState]);  
// Game loop
  useEffect(() => {
    if (gameState === GAME_STATES.PLAYING) {
      gameLoopRef.current = setInterval(() => {
        if (direction.x !== 0 || direction.y !== 0) {
          moveShiro(direction);
        }
      }, 150);
    } else {
      clearInterval(gameLoopRef.current);
    }
    return () => clearInterval(gameLoopRef.current);
  }, [gameState, direction, moveShiro]);

  // Timer del juego
  useEffect(() => {
    if (gameState === GAME_STATES.PLAYING) {
      const timer = setInterval(() => setGameTime(t => t + 1), 1000);
      return () => clearInterval(timer);
    }
  }, [gameState]);

  // Power mode timer
  useEffect(() => {
    if (powerMode && powerModeTimer > 0) {
      powerModeRef.current = setTimeout(() => {
        setPowerModeTimer(t => t - 100);
      }, 100);
    } else if (powerMode && powerModeTimer <= 0) {
      setPowerMode(false);
      setGhosts(prevGhosts => 
        prevGhosts.map(ghost => ({ ...ghost, mode: 'chase' }))
      );
    }
    return () => clearTimeout(powerModeRef.current);
  }, [powerMode, powerModeTimer]);

  const renderCell = (cell, x, y) => {
    let className = 'cell';
    let content = '';
    
    if (cell === 1) className += ' wall';
    else if (cell === 2) content = '·';
    else if (cell === 3) content = '●';
    
    if (shiroPosition.x === x && shiroPosition.y === y) {
      content = <img src={protagonistaImg} alt="Shiro Neko" className="protagonist-img" />;
      className += ' shiro';
      if (powerMode) className += ' power-mode';
    }
    
    const ghostAtPosition = ghosts.find(ghost => ghost.x === x && ghost.y === y);
    if (ghostAtPosition && !(shiroPosition.x === x && shiroPosition.y === y)) {
      content = ghostAtPosition.type.emoji;
      className += ` ghost ${ghostAtPosition.mode}`;
    }
    
    return (
      <div key={`${x}-${y}`} className={className}>
        {content}
      </div>
    );
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getGameStateMessage = () => {
    switch (gameState) {
      case GAME_STATES.READY:
        return "¡Presiona ESPACIO para comenzar!";
      case GAME_STATES.PAUSED:
        return "PAUSADO - Presiona ESPACIO para continuar";
      case GAME_STATES.GAME_OVER:
        return "GAME OVER - Presiona R para reiniciar";
      default:
        return "";
    }
  };

  // Interfaz móvil
  if (isMobile) {
    return (
      <div className="mobile-app">
        <div className="mobile-header">
          <div className="avatar-container">
            <img src={protagonistaImg} alt="Shiro Neko" className="avatar-img" />
          </div>
        </div>

        <div className="mobile-stats">
          <div className="stat-bar score-bar">
            <span className="stat-label">Puntuación:</span>
            <span className="stat-value">{score.toLocaleString()}</span>
          </div>
          <div className="stat-bar record-bar">
            <span className="stat-label">Récord:</span>
            <span className="stat-value">{highScore.toLocaleString()}</span>
          </div>
          <div className="stat-bar lives-bar">
            <span className="stat-label">Vidas:</span>
            <span className="stat-value">{'❤️'.repeat(lives)}</span>
          </div>
          <div className="stat-bar level-bar">
            <span className="stat-label">Nivel:</span>
            <span className="stat-value">{level}</span>
          </div>
          <div className="stat-bar time-bar">
            <span className="stat-label">Tiempo:</span>
            <span className="stat-value">{formatTime(gameTime)}</span>
          </div>
        </div>

        <div className="mobile-message">
          <div className="mobile-instruction">📱 Desliza para mover | Toca botones para controlar</div>
          <div className="mobile-description">Come todos los puntos y evita los fantasmas!</div>
          {powerMode && <div className="power-info-mobile">¡MODO PODER ACTIVO!</div>}
        </div>    
    <div className="mobile-game-container">
          <div 
            className="mobile-game-board"
            onTouchStart={handleTouchStart}
            onTouchEnd={handleTouchEnd}
          >
            {maze.map((row, y) =>
              row.map((cell, x) => renderCell(cell, x, y))
            )}
          </div>
        </div>

        <div className="mobile-controls-new">
          <div className="d-pad-container">
            <div className="d-pad">
              <button 
                className="d-pad-btn up"
                onTouchStart={(e) => {
                  e.preventDefault();
                  setDirection({ x: 0, y: -1 });
                }}
              >
                ⬆️
              </button>
              <div className="d-pad-middle">
                <button 
                  className="d-pad-btn left"
                  onTouchStart={(e) => {
                    e.preventDefault();
                    setDirection({ x: -1, y: 0 });
                  }}
                >
                  ⬅️
                </button>
                <div className="d-pad-center">📱</div>
                <button 
                  className="d-pad-btn right"
                  onTouchStart={(e) => {
                    e.preventDefault();
                    setDirection({ x: 1, y: 0 });
                  }}
                >
                  ➡️
                </button>
              </div>
              <button 
                className="d-pad-btn down"
                onTouchStart={(e) => {
                  e.preventDefault();
                  setDirection({ x: 0, y: 1 });
                }}
              >
                ⬇️
              </button>
            </div>
            <div className="control-label">Desliza para mover</div>
          </div>

          <div className="action-buttons">
            <button 
              className="action-btn pause-btn-new"
              onClick={() => {
                if (gameState === GAME_STATES.PLAYING) {
                  setGameState(GAME_STATES.PAUSED);
                } else if (gameState === GAME_STATES.PAUSED) {
                  setGameState(GAME_STATES.PLAYING);
                } else if (gameState === GAME_STATES.READY) {
                  setGameState(GAME_STATES.PLAYING);
                }
              }}
            >
              {gameState === GAME_STATES.PLAYING ? '⏸️' : '▶️'}
            </button>
          </div>
        </div>

        {gameState !== GAME_STATES.PLAYING && gameState !== GAME_STATES.PAUSED && (
          <div className="mobile-game-message">
            {getGameStateMessage()}
          </div>
        )}
      </div>
    );
  } // Interfaz desktop
  return (
    <div className="App">
      <div className="game-info">
        <h1>🐱 Shiro Neko Pacman 🐱</h1>
        <div className="game-stats">
          <div className="score">Puntuación: {score.toLocaleString()}</div>
          <div className="high-score">Récord: {highScore.toLocaleString()}</div>
          <div className="lives">Vidas: {'❤️'.repeat(lives)}</div>
          <div className="level">Nivel: {level}</div>
          <div className="time">Tiempo: {formatTime(gameTime)}</div>
        </div>

        {powerMode && (
          <div className="power-timer">
            🌟 Power Mode: {Math.ceil(powerModeTimer / 1000)}s
          </div>
        )}

        <div className="game-state-message">
          {getGameStateMessage()}
        </div>

        <div className="controls">
          <div>🎮 Flechas: Mover | ESPACIO: Pausa | R: Reiniciar</div>
          <div>Come todos los puntos y evita los fantasmas!</div>
          {powerMode && <div className="power-info">¡MODO PODER ACTIVO!</div>}
        </div>
      </div>

      <div className="game-container">
        <div className="game-board">
          {maze.map((row, y) =>
            row.map((cell, x) => renderCell(cell, x, y))
          )}
        </div>
      </div>

      <div className="game-footer">
        <div className="ghost-info">
          <div className="ghost-legend">
            <div className="ghost-card">
              <span>👻 {GHOST_TYPES.BLINKY.name}</span>
            </div>
            <div className="ghost-card">
              <span>🟣 {GHOST_TYPES.PINKY.name}</span>
            </div>
            <div className="ghost-card">
              <span>🔵 {GHOST_TYPES.INKY.name}</span>
            </div>
            <div className="ghost-card">
              <span>🟠 {GHOST_TYPES.CLYDE.name}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
